package phone

import "testing"

func TestPdu_Scan(t *testing.T) {
	pdu := NewPDU()
	pdu.Scan("")
	if pdu.center != "+8613800290505" {
		t.Error("pdu.center error")
	}
	if pdu.from != "106910914105639" {
		t.Error("pdu.from error")
	}
	if pdu.content != "鄑б⍼【luckin coffee】我猜你想喝点什么，送你5.5折全场饮品券，全场饮品皆可使用～去APP/小程序喝一杯 回TD退订" {
		t.Error("pdu.content error")
	}
}